import React, { Component } from "react";
import ReszvetelService from "../service/ReszvetelService";
import SearchResults from "react-filter-search";

class EredmenyComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      reszvetelek: [],
      search: "",
    };
  }
  componentDidMount() {
    ReszvetelService.getResztvetelek().then((res) => {
      this.setState({
        reszvetelek: res.data,
      });
      console.log(this.state.reszvetelek);
    });
  }
  handleSearch = (event) => {
    this.setState({ search: event.target.value });
    console.log(this.state.search);
  };
  render() {
    return (
      <div>
        <input
          style={{ margin: "0 auto" }}
          placeholder="Introduci rasa"
          type="text"
          onChange={(e) => this.handleSearch(e)}
        />
        <SearchResults
          value={this.state.search}
          data={this.state.fajtak}
          renderResults={(results) => (
            <div className="fajtaContainer">
              {" "}
              {results.map((fajta) => (
                <article className="initialBox" key={fajta.id}>
                  <div>
                    <img
                      className="fajtaImg"
                      src={"../img/fajtak/" + fajta.kep}
                      alt=""
                    />
                  </div>
                  <div className="leiras">
                    <div>
                      <h2>{fajta.fajta}</h2>
                      <div className="szoveg">{fajta.bemutatkozo}</div>
                    </div>
                    <button id={fajta.rovidites} className="button"></button>
                  </div>
                </article>
              ))}
            </div>
          )}
        />
        
      </div>
    );
  }
}

export default EredmenyComponent;
